import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Image,
  StyleSheet,
  ScrollView,
  Dimensions,
  PermissionsAndroid,
  Platform,
} from 'react-native';
import { launchImageLibrary } from 'react-native-image-picker';
import api from '../../api/axios';

const { height } = Dimensions.get('window');

const DrawingScreen = ({ goTo }) => {
  const [imageUri, setImageUri] = useState(null);
  const [imageBase64, setImageBase64] = useState(null);
  const [inputText, setInputText] = useState('');

  const requestPermission = async () => {
    if (Platform.OS === 'android') {
      const sdkVersion = Platform.constants?.Release || parseInt(Platform.Version, 10);
      
      let granted;
      if (sdkVersion >= 13) {
        granted = await PermissionsAndroid.request(
          PermissionsAndroid.PERMISSIONS.READ_MEDIA_IMAGES,
        );
      } else {
        granted = await PermissionsAndroid.request(
          PermissionsAndroid.PERMISSIONS.READ_EXTERNAL_STORAGE,
        );
      }
      return granted === PermissionsAndroid.RESULTS.GRANTED;
    }
    return true;
  };

  const handleFakePick = () => {
    const dummyBase64 =
      '';

    setImageBase64(dummyBase64);
    setImageUri('data:image/png;base64,' + dummyBase64);
  };
  const handleImagePick = async () => {
    const hasPermission = await requestPermission();
    if (!hasPermission) return;

    launchImageLibrary(
      { mediaType: 'photo', includeBase64: true },
      (response) => {
        if (response.didCancel || response.errorCode) return;
        const asset = response.assets?.[0];
        if (asset?.uri && asset?.base64) {
          setImageUri(asset.uri);
          setImageBase64(asset.base64);
        }
      }
    );
  };

  const handleSubmit = () => {
    if (!imageBase64) return;

    // 이미지는 base64 포맷으로 준비됨
    goTo('Loading', {
      imageData: `data:image/jpeg;base64,${imageBase64}`,
      text: inputText,
    });
  };

  return (
    <ScrollView style={styles.container} keyboardShouldPersistTaps="handled">
      <Text style={styles.date}>April 26, 2025</Text>
      <Text style={styles.guide}>Upload how today’s weather makes you feel.</Text>

      <TouchableOpacity style={styles.canvasWrapper} onPress={handleImagePick}>
        {imageUri ? (
          <Image
            source={{ uri: imageUri }}
            style={styles.image}
            resizeMode="cover"
          />
        ) : (
          <Text style={styles.imagePlaceholder}>Tap to select image</Text>
        )}
      </TouchableOpacity>

      <TextInput
        style={styles.input}
        placeholder="How was your day?"
        value={inputText}
        onChangeText={setInputText}
        multiline
        placeholderTextColor="#999"
      />
      <TouchableOpacity style={styles.saveButton} onPress={handleFakePick}>
        <Text style={styles.saveButtonText}>Use Dummy Image</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.saveButton} onPress={handleSubmit}>
        <Text style={styles.saveButtonText}>Save a mood diary</Text>
      </TouchableOpacity>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F9F5F3',
    paddingTop: 40,
    paddingHorizontal: 20,
  },
  date: {
    fontSize: 18,
    fontWeight: '600',
    color: '#222',
    marginBottom: 6,
    textAlign: 'center',
  },
  guide: {
    fontSize: 14,
    color: '#D26400',
    marginBottom: 12,
    textAlign: 'center',
  },
  canvasWrapper: {
    width: '100%',
    height: height * 0.35,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 12,
    backgroundColor: '#fff',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 12,
  },
  image: {
    width: '100%',
    height: '100%',
    borderRadius: 12,
  },
  imagePlaceholder: {
    color: '#aaa',
    fontSize: 14,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 8,
    padding: 12,
    minHeight: 80,
    textAlignVertical: 'top',
    marginBottom: 16,
    color: '#000',
  },
  saveButton: {
    backgroundColor: '#333',
    borderRadius: 32,
    paddingVertical: 16,
    alignItems: 'center',
    marginTop: 8,
  },
  saveButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '600',
  },
});

export default DrawingScreen;
